mapper.select:
select

from cmd_freq

	
		and `name` = #{name,jdbcType=VARCHAR}
	
	
		and tag = #{tag,jdbcType=INTEGER}
	
	
		and `length` = #{length,jdbcType=INTEGER}
	
	
		and freq = #{freq,jdbcType=INTEGER}
	
	
		and symbol_rate = #{symbolRate,jdbcType=INTEGER}
	
	
		and constellation_mapping = #{constellationMapping,jdbcType=INTEGER}
	
	
		and terminal_number = #{terminalNumber,jdbcType=INTEGER}
	
	
		and reserved = #{reserved,jdbcType=INTEGER}
	
	
		and resource_address = #{resourceAddress,jdbcType=VARCHAR}
	


	
		order by ${sort} ${order}
	
	
		order by id desc
	


	limit #{begin},#{pageSize}

select count(id) from cmd_freq

	
		and `name` = #{name,jdbcType=VARCHAR}
	
	
		and tag = #{tag,jdbcType=INTEGER}
	
	
		and `length` = #{length,jdbcType=INTEGER}
	
	
		and freq = #{freq,jdbcType=INTEGER}
	
	
		and symbol_rate = #{symbolRate,jdbcType=INTEGER}
	
	
		and constellation_mapping = #{constellationMapping,jdbcType=INTEGER}
	
	
		and terminal_number = #{terminalNumber,jdbcType=INTEGER}
	
	
		and reserved = #{reserved,jdbcType=INTEGER}
	
	
		and resource_address = #{resourceAddress,jdbcType=VARCHAR}
