mapper.select:
select

from cmd_reback_period

	
		and `name` = #{name,jdbcType=VARCHAR}
	
	
		and tag = #{tag,jdbcType=INTEGER}
	
	
		and `length` = #{length,jdbcType=INTEGER}
	
	
		and reback_period = #{rebackPeriod,jdbcType=INTEGER}
	
	
		and terminal_number = #{terminalNumber,jdbcType=INTEGER}
	
	
		and reserved = #{reserved,jdbcType=INTEGER}
	
	
		and resource_code = #{resourceCode,jdbcType=VARCHAR}
	


	
		order by ${sort} ${order}
	
	
		order by id desc
	


	limit #{begin},#{pageSize}

select count(id) from cmd_reback_period

	
		and `name` = #{name,jdbcType=VARCHAR}
	
	
		and tag = #{tag,jdbcType=INTEGER}
	
	
		and `length` = #{length,jdbcType=INTEGER}
	
	
		and reback_period = #{rebackPeriod,jdbcType=INTEGER}
	
	
		and terminal_number = #{terminalNumber,jdbcType=INTEGER}
	
	
		and reserved = #{reserved,jdbcType=INTEGER}
	
	
		and resource_code = #{resourceCode,jdbcType=VARCHAR}
