mapper.select:
select

from cmd_resource_code

	
		and `name` = #{name,jdbcType=VARCHAR}
	
	
		and tag = #{tag,jdbcType=INTEGER}
	
	
		and `length` = #{length,jdbcType=INTEGER}
	
	
		and terminal_address_length = #{terminalAddressLength,jdbcType=INTEGER}
	
	
		and terminal_address = #{terminalAddress,jdbcType=VARCHAR}
	
	
		and reserved = #{reserved,jdbcType=INTEGER}
	
	
		and resource_code = #{resourceCode,jdbcType=VARCHAR}
	


	
		order by ${sort} ${order}
	
	
		order by id desc
	


	limit #{begin},#{pageSize}

select count(id) from cmd_resource_code

	
		and `name` = #{name,jdbcType=VARCHAR}
	
	
		and tag = #{tag,jdbcType=INTEGER}
	
	
		and `length` = #{length,jdbcType=INTEGER}
	
	
		and terminal_address_length = #{terminalAddressLength,jdbcType=INTEGER}
	
	
		and terminal_address = #{terminalAddress,jdbcType=VARCHAR}
	
	
		and reserved = #{reserved,jdbcType=INTEGER}
	
	
		and resource_code = #{resourceCode,jdbcType=VARCHAR}
